if not VoiceOver or not VoiceOver.DataModules then return end
AI_VoiceOverData_Vanilla.GossipLookupByNPCName = {
	["Городская стража Штормграда"] = {
		["Леди Хотешем принимает посетителей в королевском зале для аудиенций. Она проводит вас в Низину Арати, где Лига Аратора нуждается в помощи в боях с Осквернителями."] = "9ff1ef7f7918452a7571c3f770ab4e14",
		["Да, Среброкрылым пригодится твоя помощь. Поговори с Эльфарран в зале аудиенций крепости Штормграда."] = "ab67b49961ce98725cccb8a6b0e8e7ef",
		["Тогда вам нужен Тельман Сланцевый Кулак. Он находится подле короля в крепости Штормграда."] = "bcfcce27aafd9e54ef3d2fcdb27f4139",
		["Лукан Корделл держит магазинчик на берегу канала возле тюрьмы. Я уверен, что он сможет научить тебя искусству зачаровывания."] = "91e3f367dbb0e83b362a5880d1271dc4",
		["Ничто не сравнится с днем, проведенным за рыбалкой на озере Хрустальном. Меня обучил этому искусству Арнольд Лиланд. Обычно он стоит на причале в районе каналов и рыбачит целый день.  А, да, и не верь этим его рассказам о монстрах в каналах."] = "852f678dbc06d3601bdf168e7032735a",
		["В 'Тканях Дункана' работает один из самых лучших портных Штормграда – Джорджио Болеро. Обязательно посетите этот магазин в Квартале Магов!"] = "8706ad14fe81bd7bfe9d6986ff68ed63",
		["Если вы хотите научиться искусству снятия шкур, отправляйтесь в Старый город и загляните в 'Прочную шкуру'."] = "6f55eb7e6eebe144bc957790dae41464",
		["Поговорите с Гельманом Камнеруком. Кажется, он живет в Квартале Дворфов, но точно не скажу… Посмотрите там."] = "8f8f48c440d58b1a783b868dd9222e74",
		["Хочешь научиться кожевничеству? Тогда тебе нужен Саймон Дубильщик. Его можно найти в 'Прочной шкуре', что в Старом городе."] = "52ef93000b10574ffb40f6f451597e26",
		["Таннису можно найти у магазина 'Все для алхимика', что в Квартале Магов."] = "356b1700581f2c27333ab266a26bb077",
		["В Храме Света собираются целители со всего Штормграда. Найди там Шайну Корпию – она обучит тебя тонкостям своей профессии."] = "fd43bf9ef9a4ea5b42ea21a6017c205b",
		["Инженерному делу в Штормграде обучает гном по имени Лилиам Шпиндель. Его можно найти в задней части Квартала Дворфов."] = "05e90b70ee82c6ab15e68fec597454c2",
		["Если хочешь научиться отлично готовить, отправляйся в таверну 'Свинья и свисток' в старом городе и поговори со Стивеном Райбеком. Уж он-то умеет приготовить такие ребрышки, как никто другой.  Эх… даже слюнки потекли из-за тебя. Но не рассчитывай, что он тебя и это блюдо выучит готовить!"] = "dbe19e29f297c8e1fb35a95e50f89466",
		["Терум Подземная Кузня – как раз тот дворф, который тебе нужен. Один из лучших местных кузнецов, скажу я тебе. Ищи его в центре Квартала Дворфов."] = "a4956fb4f50c46eaea4f68dd50ceb194",
		["Во всем Штормграде есть только одно место, интересное для алхимиков… Оно так и называется: 'Все для алхимика'. Ты найдешь его в Квартале магов, на внешнем кольце магазинов."] = "7034523efe4bfc8203b790a1d127dca2",
		["Что? Я не знаю никаких наставников чернокнижников в Штормграде.  Хотя… Были признаки бесовской деятельности возле 'Зарезанного ягненка' в Квартале магов. Можешь поискать там."] = "f6a52f523eecff532c9530eae7d0e89a",
		["Торфин Камнещит проделал долгий путь из Стальгорна к нам, чтобы донести до нас свои знания об охоте. Если ты не против подышать дымным воздухом Квартала Дворфов, ты найдешь его в его доме в северо-восточной части района."] = "8c8ac67be8a32d83d11e1119cbe8b9f8",
		["Если вы хотите стать великим паладином, лучшего учителя, чем лорд Грейсон Тенелом, вам не найти. Грейсона можно встретить в Храме Света на Соборной площади."] = "2d22a7afddcc8d88b9c1353ae28daf09",
		["Храм Света на Соборной площади – это дом жречества Штормграда. Там ты найдешь верховную жрицу Лорену."] = "bb897a746e8a30c2b211e9076b6a286d",
		["Ну, Теридана и Малдрина обычно можно встретить в Парке у Лунного колодца. Отправляйся в западную часть Штормграда – ту, что к северу от Квартала магов и к западу от Соборной площади."] = "2c1e9110dff51f5d1d491b13523c5989",
		["Воинов можно встретить либо в таверне 'Свинья и свисток', либо в казармах Старого города. И мой тебе совет: сначала сходи в таверну."] = "9434f3b21efa2de084243d600d530ff5",
		["Значит, тебе надо поговорить с Осборном. Он – наилучший учитель искусства незаметности в Штормграде. Ищи его в Старом городе."] = "5079ccedcf83df1e6554116dbaa337bd",
		["Маги обычно собираются в Башне магов, в Квартале Магов. У них там такая башня! Рядом с ней наши казармы выглядят просто сараями какими-то."] = "338c8fab07f57033e48217d5973c15df",
		["Какое поле боя вас интересует?"] = "481f209702cc74fc87fe4569d25b276b",
		["Какая профессия вас интересует?"] = "ce83ae92effc9c6509cef60c5f919389",
		["Какой класс вас интересует?"] = "7e357056bac5b73342ac2bee0458e300",
		["А ты уже, значит, Поборник Альянса?! Полагаю, сейчас ты захочешь отправиться в Зал Защитника. Это в Старом городе, но без офицерского звания тебя внутрь не пустят – придется довольствоваться разговором с офицером Арейн. Иди, не задерживайся."] = "33278f8101e800c93fc3d2385cfed699",
		["Тебе нужна Дженова Камнещит из Квартала Дворфов. Она заботится о питомцах охотников, пока те учатся у ее отца."] = "9654c77653b91d446f531295982fbec7",
		["Ву Пинг невероятно вынослив. Глядя, как он несколько часов подряд тренируется с оружием, я начал жалеть учебный манекен. Лучшего учителя оружейных навыков и придумать трудно."] = "312810e5deb14cd60aa19428f7f51227",
		["'Почтовый ящик находится между банком Штормграда и трактиром 'Позолоченная роза' в Торговом квартале'."] = "bb63ff9d75c7726f1d06e21fe2021b8b",
		["В Штормграде много заведений, где можно пропустить стаканчик, но хороший ночлег есть только в одном. Отправляйтесь в 'Позолоченную розу' в Торговом квартале. Там умеренные цены и чистое постельное белье."] = "44af32f368e03c09b89fe9f87be3875e",
		["Это в Торговом квартале. В прямой видимости от ворот Штормграда."] = "568c01f3e19d99d5f41ea3aac2056ebe",
		["Не хватает смелости для полета на грифоне, а? К счастью для тебя, у нас есть вариант, при котором тебе не придется отрываться от земли… Ну, более или менее. Это гномский перевозчик людей, подземный поезд, удобно расположенный сзади района гномов."] = "b8a17986e4d6330f2e74f8f8a53c0d86",
		["Альдвина Лолкинса можно найти в гостевом центре Штормграда. Это в Торговом квартале."] = "53cfbdba892d116c2e03388c9fd6d5d0",
		["Укротителя грифонов можно найти на северо-восточном валу Аллеи Героев."] = "f5c1db946eb2f4fba13e922275d9fb95",
		["Банк Штормграда находится в Торговом квартале, на северо-запад от городских ворот. Вот, позвольте мне показать на карте."] = "8fedf8298f3edb222d5b5f4355c58675",
		["Куда вы хотите пройти?"] = "2402ec9453525b4ec16e1180457c7cfb",
		["То, что Лига Аратора пытается сделать в Низине Арати, просто замечательно. К сожалению, им мешает нежить и ее приспешники из Орды. Направляйся в Крепость Штормграда, леди Хотешем ожидает тебя в в зале для аудиенций. Она поможет тебе попасть в Низину Арати."] = "adb9b1e902dc3d39490b7c0e4c0e67cd",
		["Среброкрылым Часовым понадобится вся возможная помощь в бою с Всадниками Песни Войны. $n, если вы готовы им помочь, отправляйтесь в крепость Штормграда и поговорите с Эльфарран."] = "a28ad28e180fb23f6b2f30a8926dd8d3",
		["Как бы мне хотелось пойти с вами, $n. Отправляйтесь в крепость Штормграда, там в зале для аудиенций найдите Тельмана Сланцевого Кулака. Он поможет вам добраться в Альтеракскую долину."] = "5da822272073a4d726872184c3421ca6",
		["А, ты ищешь Лукана Корделла. Он держит лавку на внешней стороне Квартала Магов, возле дороги к тюрьме."] = "e3f9bcedb2188218c92b8b8fb1e8eb6a",
		["Этот лодырь Леланд… что сидит на причале возле Торгового квартала и день-деньской удит рыбу. За все то время, что он рыбачит, можно было тридцать раз стать настоящим мастером этого дела. Так что вам надо с ним поговорить."] = "2472ef40c4054390e47d26a2790dcef8",
		["Портняжное дело, говоришь? Ну, рано или поздно ты в любом случае узнаешь о Джорджио Болеро. Это наш местный законодатель мод. Если хочешь пойти к нему в ученики, отправляйся в Квартал Магов, на внешнее кольцо."] = "9cba4dc5407b5b21f860b325ab149a86",
		["Вы знаете, я могу пронзить орка насквозь или нарезать тролля на кусочки? Могу даже вырезать свои инициалы на боку огра, но вот шкуры сдирать – это не по мне. Поди пойми.   Однако если у вас на это смелости хватает, то поговорите с Марисой Гренжер в 'Защитной шкуре' в Старом городе."] = "c7ce82c152a63bc669f1d309ce17f9bd",
		["В духе дворфов прислать специалиста по шахтам из Стальгорна, чтобы научить нас уму-разуму. Разве сложно взять кирку и долбить по камню? Ну, раз уж он здесь, он, наверное, как раз тот, кто тебе нужен. Его зовут Гельман, и проживает он в Квартале Дворфов."] = "64070f95813e914bdca6f15e390c919e",
		["Интересуешься кожей, да? Видимо, тебе нужно поговорить с Саймоном Дубильщиком из Старого города. У него тут лавка 'Прочная шкура'."] = "02b8fd48e20c73453938adc9f4995c2c",
		["Ничто не сравнится с общением с природой… по крайней мере, так говорят ночные эльфы. Я не особо люблю разговаривать с деревьями и цветами, но Танниса могла бы вам помочь. Она в Квартале магов, возле 'Все для алхимика'."] = "42747362bb8e6d2164d7e79747248058",
		["Обучение первой помощи лучше всего проходить в Храме Света, под бдительным присмотром Шайны Корпии.   Желтые крыши Храмового двора и шпиль самого храма видно из любой точки города."] = "b1be3018807cc2da500145c8c2b10794",
		["Ой, это мне напомнило, что мне нужно починить свои реактивные сапоги. Мудрым на заметку, мой друг: реактивные сапоги и грифоны плохо сочетаются.  Ой, тебе же нужно объяснить, как пройти… просто направляйся в Квартал Дворфов, ты найдешь Лилиама Шпинделя дальше, в глубине."] = "bb3fee63f3293484e591674a691d3c88",
		["В таверне 'Свинья и свисток', что в Старом городе, есть превосходный повар. Я наблюдал, как он управляется с ножом. Между нами говоря, не думаю, что он всегда был поваром… Но если ты действительно хочешь учиться, обратись к нему."] = "af66b0b5d2d42f34571240ac0c887807",
		["Ты что, $gсам:сама; не чуешь запах дыма из дворфийских горнов? Если хочешь учиться кузнечному делу, поищи Терума Подземную Кузню. Он сейчас в Районе гномов, это прямо на север от Соборной площади."] = "a3a18a74f32a8ebab3109103db8d0fd5",
		["В Квартале Магов есть маленький магазинчик под названием 'Все для алхимика'. Вообще-то там полно всяких маленьких магазинчиков, но вы идите по внешнему кругу – точно не пропустите."] = "8651024fca6f7bd42a0336d52e7d69fc",
		["Чернокнижники, да. Дошли до меня слухи, что собираются они в баре под названием 'Зарезанный ягненок', что в квартале магов, но я ничего тебе не говорил!"] = "f05de425d7bd02752037b8477cf01b53",
		["Охотник, значит? Ну, тогда тебе нужно поговорить с Торфином Камнещитом из Квартала Дворфов.  Да, и не забудь ему передать, что если я еще раз наступлю в сотворенное его питомцем, учить охотников он будет прямо из-за решетки в Тюрьме!"] = "d9d6fc265ce7e558402d3ca53747dac5",
		["Лорена – верховная жрица Штормграда. Ее можно найти в Храме Света на Соборной площади."] = "4a22a1b67aa8fe418d517ebb0e371796",
		["Если вы ищете наставников друидов, то вам стоит пойти в Парк. Теридана и Малдрин можно застать у Лунного колодца в центре этого района."] = "6ca4fdf838b083f58d40a21536adeaed",
		["Хотите подучиться, да? Я мог бы обучить вас куда лучше, чем любой воин Штормграда, но стражам это запрещено. Думаю, вам придется поискать наставника возле казарм или в таверне 'Свинья и свисток' в Старом городе."] = "cd7fa3fdce25e94d80b36771810f1e93",
		["Тьфу, да от этих лазутчков никакой пользы – только работы нам в Штормграде прибавляют. Взять бы этих Осборна с Матиасом из Старого города – и прямиком в тюрьму."] = "6e24adae0babfe7f1e128960d09c55af",
		["Интересуетесь тайной магией? Тогда обязательно посетите Башню магов в Квартале Магов."] = "0493aff58b480f500d524b265550d281",
		["Так-так, пробиваем себе дорогу в жизни, значит? Тебе нужен Зал Защитника, а найдешь ты его на юго-восточной окраине Старого города. Внутрь пускают только офицеров, а остальные могут поговорить с офицером Арейн. Удачи!"] = "a0e2103ae01da9c6c1707056a504c6ae",
		["Если тебе нужно временно пристроить питомца, поговори с Дженовой Камнещит из Квартала Дворфов."] = "b68ea919378ae726f910e862a386d16f",
		["Мастер Ву Пинг владеет всеми известными видами оружия! Особенно хорошо он управляется с мечом. Большую часть времени он проводит в арсенале Веллера, ожидая новых поставок оружия."] = "ea46258d498568523f3fadf071cc9af0",
		["Понятия не имею, как это это происходит… наверное, эти ящики – что-то вроде магических порталов. Тот, что возле банка, вечно толпу собирает. Представляешь, из этого маленького ящичка вытаскивают мечи, щиты, доспехи!"] = "b4b551dc6175faa3e32ad05a88b10b09",
		["Ах, 'Позолоченная Роза'… Мягкие кровати, роскошные ванны с теплой водой… только вспомню об этом трактире – и сразу туда хочется… *зевает* Везет же тебе… ты найдешь ее в северной части Торгового квартала… Мягкие подушки, теплые одеяла. *зевает*"] = "e84a11ab6b3896ecf97c8adfbc08636c",
		["Еще один предприниматель, а? Ну что ж, аукционный дом находится в Торговом квартале. Недалеко от банка."] = "3b18fc0710278c6aa12fb30a85999aaf",
		["$GСлышал:Слышала; про поезд, а? Ну и классно же на нем кататься! Он находится в дальней части Квартала Дворфов. О, главное – руки-ноги на ходу не высовывай!"] = "98484e0cd95c72af21e16247f3eead70",
		["Гильдию, значит, надумали собрать? Ну что ж, удачи!  Поговорите с Альдвином Лолкинсом в гостевом центре Штормграда. Это в Торговом квартале, возле ворот в Аллею Героев."] = "dbd178f951b41e05c59af921ec3b7725",
		["А, тебе нужен Дунгар Долгопив. У него одни из самых быстрых грифонов по эту сторону Каз Модана! Ты можешь найти его на валу в Торговом квартале."] = "a36856bf0646fdd5ce14e1bbdbeab7a2",
		["Счетная палата Штормграда расположена у передних ворот, в Торговом квартале Штормграда. И, когда устанешь считать деньги, обязательно остановись выпить в 'Позолоченной Розе'."] = "554b8726d09d342022c5c7eaf5a9818e",
		["Советую заглянуть в Храм Света на Соборной площади."] = "7c15eb60533829779dc9fd0c60d26a6a",
		["Так ты – дитя природы? Тогда тебе прямая дорога к Лунному колодцу в Парке."] = "a63f0a77a472eab18caf48ea8320d27e",
		["Посмотрите в Старом городе – в казармах или таверне 'Свинья и свисток'. Если зайдете в таверну, обязательно попробуйте тамошнего пива. Дворфийский эль и рядом не валялся, точно говорю."] = "c621f0b9a4a6a6290dd5972a302122fc",
		["Если вам нужны разбойники, отправляйтесь в Старый город. Это на северо-востоке Штормграда."] = "cd52082b0fdc76c7dded230c974a2ebf",
		["Похоже, тебе стоит поговорить с Магинором Дюма! Обычно он находится в Башне магов, в Квартале Магов. Конечно, никогда не знаешь, когда и куда эти маги отправятся через свои порталы."] = "fa0d9c855011039c74c70b23db0b82ea",
		["Аааа, ну тогда вам надо в гостевой центр Штормграда. Если войти в главные ворота из Аллеи Героев, это будет первый дом слева."] = "646cbbc2bf91bdaa020beb25f4e0a678",
		["Хочешь прокатиться на грифоне, а? Дунгар своих птичек держит на валу в Торговом квартале. Только не забывай держаться покрепче!"] = "7df84a254c23565acaa7fc468ead3ab2",
		["Хм, грифоны? Никогда ими не интересовался, ну да каждому свое.  Обратитесь к Дунгару Долгопиву в Торговом квартале."] = "258744fe85450f544093d5fd17856341"
	},
	["Маршал Макбрайд"] = {
		["Эй! Ты крепко $gсложен:сложена;, а у нас тут стражей не хватает. Нам бы не помешала твоя помощь…"] = "2343fa9b01b2ca00b5bc7a390ac07935"
	},
	["Кельден Бремен"] = {
		["С помощью строгой переквалификации мне пришлось заставить множество учеников забыть все, что они знали, чтобы я смог учить их заново. Услугу, что я предлагаю, с каждым разом все сложнее и сложнее оказать. Вот почему каждый раз при ее использовании я повышаю цену. Ты желаешь забыть все, что знаешь?"] = "f6708bdc5d32f47f0eaf4fce7cf2764c",
		["Здравствуй, $c. Вот что я тебе посоветую: будь $gосторожен:осторожна; с магией. Она может легко погубить новичка."] = "54074b0b08d3a499512ae6fba95f3115",
		["Здравствуй, маг. Тебе нужны новые знания о чудесном мире магии?"] = "3ddc082eac22c52bb34743331a694904"
