	},
	["Таттернак Стальной Горн"] = {
		["Вождь повелел мне изучать все виды оружия и доспехов. Он прислал меня в Степи, где сливаются культуры орков и тауренов, чтобы я повстречал как можно больше путешественников и узнал как можно больше об этих землях.$B$BОн говорит, что даже у самых жалких народов есть чему поучиться… даже у иглогривов и кентавров."] = "7d7cdc1000a3951e96ce7ea9aae1068e"
	},
	["Пароклапан"] = {
		["Интересно, интересно… $N, я знаю одну девушку-друида, которая живет на вершине холма над Пещерами Стенаний. Возможно, она знает больше об этом осколке.$B$BТеперь прости, но я должен вернуться к работе."] = "b553c80d00cdd9070918c489df5ae9bc",
		["Видишь, новый резак Торговой компании лучше моего последнего образца во всем – от заменяемых рук до управлямых клешней! Мой босс поставил мне очень жесткие сроки, но ни я, ни мои парни не можем ничего придумать. $b$bПоэтому мы решили 'одолжить' один из их прототипов. Я послал Визлкранка в Нефтяное болото, но он до сих пор не вернулся!"] = "3b982d83ca250aba2a857fe1a2388627",
		["Директор Ридлвокс назначил меня главным в Союзе по разработке резака. Всего несколько месяцев на новом месте – и уже неприятности! Не поможешь мне, $n?"] = "0cc088f013cb6fc40acf3a7acfed831d",
		["Еще столько всего надо сделать! Союз установил нам жесткие сроки – и лучше бы их не срывать!"] = "f04e73ec5cd32473774944eedc48a86f"
	},
	["Тонга Рунический Тотем"] = {
		["Времена, в которые мы живем, хрупки, как переменчивый ветер, $c. Важно помнить, что истинно и постоянно в этом мире, и беречь это. Все остальное неважно."] = "3c1076a9c4093b75e0ba1a3c06c09528"
	},
	["Трауг"] = {
		["Духи привели тебя ко мне… я чувствую их благословение."] = "226e4c5248a59d219e14d288106c8613"
	},
	["Джахан Соколиное Крыло"] = {
		["Степи с их жарким солнцем и враждебными обитателями – идеальное место для желающих испытать силу своего тела и духа."] = "18a3a77c5ae8b48a2bbaab1d5d833914"
	},
	["Кил'хала"] = {
		["Эй, дружище, помочь чем?"] = "b9c260f10777917d0aaa23c86b095d93"
	},
	["Заргх"] = {
		["Посмотри-ка на это мясо. Не стоит отправлятся в Степи без хорошенького запаса провизии."] = "3cbc06359ec26be5a68c387070b4ffd6"
	},
	["Оловень"] = {
		["Боюсь, теперь ты для меня слишком $gхорош:хороша;! Тебе надо встретиться с Ноггом из Оргриммара."] = "f87c37944192073e24d10f1a070e94b4",
		["Да, я покажу тебе пару вещей. Посмотрим, как ты справишься – может быть, ты даже удивишь меня!"] = "af9ccb95467b25c6dc0eaa8a4d0cf1cf",
		["Я не могу тебе помочь! Тебе нужен кто-то более опытный. Так… Ты у нас из Альянса, значит, тебе лучше пойти в Штормград к Лилиаму Шпинделю."] = "1389ded94ec799fa553e34c3557ac12c",
		["Что? Ты что-то хочешь?"] = "b0fb181dddc32fa304d6805b255e6bde"
	},
	["Кэрн Кровавое Копыто"] = {
		["А, молодой воин! Участвуешь в Детской неделе, так? Старику приятно слышать, что ты подаешь другим пример, которому они, несомненно, последуют.$B$BБуду рад принести пользу тебе и парнишке. Хмм… какой же совет ему дать? Слушай волю духов, да… ну и овощи, заодно, кушай, они полезные…"] = "b7a8229dca2676591932185ee439b12b",
		["Приветствую тебя, о $gюноша:дева;, и добро пожаловать в Громовой Утес. Здесь ты найдешь надежное убежище от бедствий и невзгод. Здесь рады всем, кто принадлежит к Орде.$B$BЗнай: духи прошлого и будущего оживают в этом священном месте. Воздай им должное – и да воздастся тебе."] = "6a6507689e26d6a64d8cd2d70050e9b6"
	},
	["Томас Миллер"] = {
		["Самые выгодные сделки во всем Штормграде, друг мой! Итак, чем я могу тебе помочь?"] = "0a50227fd4a79a221c5e74147031f06e",
		["Лучшие цены во всем Штормграде, друг мой, лучших нигде не найти. Так как, чем именно я могу помочь?"] = "0a50227fd4a79a221c5e74147031f06e",
		["Просто интересуетесь, или ищете что-то особенное? Что-нибудь подсказать?"] = "a86de99ac7614f683ab7eab24b0a0b93"
	},
	["Боуэн Брисбойс"] = {
		["Итак… желаете стать портным? Ну что ж, желание – это хорошо. Посмотрим, как у вас с возможностями."] = "aa04b66da46a80a2753c940c3f9f1a2e",
		["Я многое знаю, но экспертом меня не назовешь. Знаете, с кем вам лучше поговорить? С Рианнон. Она тут недалеко. Поищите ее в Подгороде."] = "e5260b81bc08d0a40f54722eafa81dcb",
		["Рад нашему знакомству. Могу ли я помочь вам?"] = "e291d9ad0a70ad2cbfd236c26b801d03"
	},
	["Клод Эрксин"] = {
		["Ах, $gдруг:подруга;, я помогаю только охотникам и их питомцам."] = "043d1db9fcbabde933fe81bd22b51be2",
		["Ты хочешь учиться, чтобы передать знания своим питомцам?"] = "1566d430b06beb690f54132cef76af32"
	},
	["Шелена Робарт"] = {
		["Пока не пришла чума, я кожей вообще не интересовалась. А теперь вот знаю о ней столько, что и других могу учить. Смерть подарила мне уйму времени."] = "d626b39784157d5421d92e7ddf077906",
		["У тебя все отлично получается. Мне больше нечему тебя учить, а вот Артур Мур из Квартала Разбойников Подгорода мог бы тебе помочь. Только учти: он из нежити. Передай ему, что ты от меня."] = "151ec51becd067dfa6e36901ab1189a4",
		["Кожа, чудесная кожа! Она удерживает ваши внутренности… внутри. Возможно, вы заметили, что моя кожа не так уж хороша. Подумать только, а когда-то я так переживала из-за пор и угрей! А теперь и переживать-то не из-за чего."] = "21c1a4ade87cbd580ae80aa7f1f7ae8d"
	},
	["Гийом Сорю"] = {
		["Ты хорошо $gучился:училась;, дитя мое. Отправляйся в Подгород и найди кузнеца по имени Джеймс ван Брюнт. Он научит тебя многому из того, что мне недоступно. Насколько я знаю, он обычно проводит время там, где тренируются воины, кует им оружие и доспехи."] = "1480eac77cfa26ca7f7f181a30f7627e",
		["О да, еще $gодин желающий:одна желающая; разжечь огонь и выковать орудия войны. Конечно же, я возьмусь тебя учить."] = "5c340e45de61831b7a0d003ad85ea723",
		["Вы тратите мое бесценное время. Это должно быть действительно что-то важное."] = "ba7308311ab1b7a9aa44fa79f5819089"
	},
	["Часовой Тельдрассила"] = {
		["Несомненно, в Дарнассе найдется портной, который смог бы учить тебя. Когда доберешься туда, спроси одного из часовых Дарнасса, как можно найти учителя портняжного дела."] = "fde658d3d3dda267bafe6d6134c02642",
		["Раднаал живет в своем доме у дороги в Дарнасс. Быть может, он научит тебя чему-то новому."] = "2e33db29fb989db5225ed21dd00dfc75",
		["Мастера клана Спутанная Грива работают в своем домике у дороги в Дарнасс. Надийя – отличный учитель кожевников. Обязательно возьмите у нее пару уроков."] = "f29cadd86be2633b030d2922ef0d73a4",
		["Малорн ухитряется срывать даже самые хрупкие травы, не повредив их. Ясно, что учиться тебе надо у него. Он живет в Доланааре, к востоку от таверны."] = "fa2f6a0bf9ea5e78c7cd9a9670147fcc",
		["В последнее время на озере Аль'Амет стало небезопасно, поэтому все рыбаки переехали в Дарнасс. Поищите учителя там."] = "f04c72b9d344f81d24b170747fc637e9",
		["Бьанси часто помогает стражам, патрулирующим местную глухомань. Если хочешь научиться оказывать первую помощь, обратись к ней."] = "5236c19b097c185960d21473a0f76c58",
		["Аланна Око Ворона отчасти приписывает свой талант зачаровывателя общению с духами на Поляне Оракула. Сейчас ты можешь найти ее там."] = "9963e2c8079556e7e03308c3b714ad3d",
		["Заррин, похоже, хорошо знает кулинарное дело. Почему бы тебе не пойти и не посмотреть, не научит ли он тебя нескольким рецептам? Он обычно готовит в таверне в Доланааре."] = "83e47373c7b037c97ea19a841c67728b",
		["Когда Циндра не кипит от ярости, как ее зелья, она вполне мила и может многому научить. Ты найдешь ее к востоку от таверны Доланаара."] = "64eab3bd0b6e3e418b180b01fb4cd9a0",
		["Кайра Ветрорез обладает невероятным мастерством в обращении с клинком. Она может научить тебя кое-чему. Ты можешь найти ее в таверне в Доланааре."] = "2ff6748c5381da2b47cccf19dd9e9d98",
		["Разбойник? Я не имею ничего общего с такими типами, но, думаю, Джаннок – тот, кто тебе нужен. Ищи его в таверне Доланаара."] = "83667d262f35fc73bb7553229dba8a25",
		["Жрица по имени Лорна Утренний Свет называет Доланаар своим домом. Кажется, ее можно встретить к северу от таверны."] = "2c6cd31df344e81e13802e0526bd4f82",
		["Если вы ищете охотника, вам нужен Далазар. Обычно его можно найти возле таверны в Доланааре."] = "6a033c0f5e434887661d585271c95874",
		["Ты ищешь помощи друидов? Тогда тебе нужен Калль. Его можно встретить возле Лунного колодца, к югу от трактира Доланаара."] = "7c41a2cdee58db931c3f57aaeecede92",
		["Путь того, кто выбрал себе профессию, долог и прибылен. Какую профессию вы выбрали для себя?"] = "d23c8b801de15e2ee7f38ac7e99b1867",
		["В Доланааре ты найдешь множество наставников, посвятивших своему делу долгие годы. Чему именно ты хочешь учиться?"] = "3b62e4181d8e155be0d34c32038137f0",
		["А, добрая Сериадна… Все животные чувствуют себя в безопасности рядом с ней. Ты найдешь ее прямо возле таверны в Доланааре."] = "4b9bee6beacd2d5faf637d1fc7af3558",
		["Хорошо отдохнуть и выспаться наверняка можно в кроватях, которые хозяин таверны Кельдамир держит в таверне в Доланааре."] = "94a6749341ef5f66c12f93180c75a230",
		["Такие дела решаются только в Дарнассе. Если вы хотите создать гильдию, отправляйтесь туда."] = "d02f310024399482b9dac67cfc148f64",
		["Чтобы добраться до руттеранской переправы, тебе нужно прийти в Дарнасс и воспользоваться порталом в деревню Рут'теран, расположенную у подножия Тельдрассила."] = "f7f50698e64399e73c940b8e5999f55e",
		["Банк находится в безмятежном городе Дарнассе. Тебе придется пойти туда, если хочешь оставить свое имущество на хранение."] = "6427b5ad172185229fdf52a2a91052ea",
		["В Тельдрассиле есть много интересных мест. Что именно ты ищешь?"] = "274ed111b1b971c19cf221ceb6d4da6d",
		["В Дарнассе производится отличное текстильное сырье. Неудивительно, что все лучшие портные перебрались туда. Если ты хочешь освоить портняжное дело, отправляйся в Дарнасс."] = "565828b0bf2c0a029bdd705634e05c4c",
		["Надийя работает с кожей уже много лет. Чтобы найти ее, пройди на запад по дороге в Дарнасс. Может быть, она научит тебя тонкостям своего ремесла."] = "59c29a24054e9781525056418704352b",
		["Травничество требует осторожного подхода. Этому учат немногие – в том числе Малорн. Его можно найти восточнее таверны в Доланааре."] = "80ed9b7cf270b2683ac103fb42181eba",
		["Древесники разогнали всех рыбаков в окрестностях Доланаара, поэтому учителей рыбной ловли надо искать в Дарнассе."] = "1806edb01b654109c85d847d2fc7129a",
		["Бьанси – настоящий мастер первой помощи. Если хочешь взять у нее несколько уроков, ступай к трактиру Доланаара, а оттуда – на север."] = "51ebad09e83f3656e4c78bc259beef52",
		["Аланна целыми днями совершенствует свое мастерство на поляне Оракула. Если у кого и учиться зачаровыванию, так это у нее."] = "15211b4d4b796d136e52a122b432560f",
		["Еще один повар в Доланааре не помешает, хотя у нас уже есть Заррин. Вы бы сходили к нему – вдруг чему полезному научит?"] = "0a5ce5d8a7759f29e79e96556ab20845",
		["Познания Циндры в алхимии не слишком глубоки, но для начала и это сгодится. Ты найдешь ее к востоку таверны в Доланааре."] = "2f7ad8bfc99055a560658a285e3618b3",
		["Однажды мне посчастливилось увидеть Кайру Ветрорез в бою. Поэтому я не сомневаюсь, что она хорошо тебя обучит. Найти ее можно в трактире Доланаара."] = "c71b255b624a3a50ff4387076cd62716",
		["Ищете разбойника? Ну, тогда вам нужен Джаннок. Поговорите с ним, он в доланаарском трактире."] = "7ee91439488e010165881cb797efaa1e",
		["Лорна Утренний Свет лечит всех жителей Доланаара, которые в этом нуждаются. Многим жрецам стоит у нее поучиться."] = "4884b53e55dec4a3470d470b3fa83514",
		["Дазалар – самый известный охотник Доланаара. Почему бы тебе не поговорить с ним?"] = "a50cc4748a430d5984fd12118582d8e4",
		["Друид Кел проводит много времени у лунного колодца в Доланааре. Я уверена, что он мог бы научить тебя тому, что тебе нужно."] = "16bba8106fe5aa116151356d9d0c1668",
		["Тебе нужен кто-нибудь, умеющий ухаживать за животными? Тогда поищи Сериадну возле трактира Доланаара. Она тебе и нужна."] = "f973dc94f8e2f3e5bb9ad0a919636750",
		["Если тебе нужно время на восстановление сил, лучше всего его провести в таверне Доланаара. Хозяин таверны Кельдамир позаботится о твоем отдыхе."] = "7044a367e4a4d0f456163951264eed1d",
		["Такие документы, как бумаги на создание гильдии, могут быть получены от мастера гильдии в городе Дарнассе."] = "073009f4d29320fba71c2b204da1fac8",
		["Паром руттеранской переправы отправляется в Аубердин из деревни Рут'теран у подножия Тельдрассила. В эту деревню можно попасть из Дарнасса через портал."] = "6c037df3b7038d9b82a973b662d9e670",
		["Если тебе нужен банк, ступай в самое сердце Дарнасса. Там ты найдешь надежное хранилище для своих сокровищ."] = "4ea5d9544049803bcffe33d330e2fb7d"
	},
	["Алайссия"] = {
		["С помощью строгой переквалификации мне пришлось заставить множество учеников забыть все, что они знали, чтобы я смогла учить их заново. Услугу, что я предлагаю, с каждым разом все сложнее и сложнее оказать. Вот почему каждый раз при ее использовании я повышаю цену. Ты желаешь забыть все, что знаешь?"] = "002e3e2a721f2f5fb8a4b9da6cdeb661",
		["Я не вижу в тебе воинского духа, дитя. Ты $gприрожденный:прирожденная; $c, и мне нечему тебя учить."] = "aff660250ac92075478bdf7475083504",
		["Приветствую. Ты желаешь выучиться искусству сражения?"] = "d21f68288750cb295d23c5dd3f5d206b"
	},
	["Фрагун Шепот Тени"] = {
		["Приветствую, $gюный разбойник:юная лазутчица;. Что ты ищешь?"] = "359b565e3f9598b0c4c99d31465a6002",
		["Мы лишь темные тени. Вечные тени. Невидимые."] = "8bcc28a32ab2390c6dbf3ac05dc5341e"
	},
	["Шанда"] = {
		["С помощью строгой переквалификации мне пришлось заставить множество учеников забыть все, что они знали, чтобы я смогла учить их заново. Услугу, что я предлагаю, с каждым разом все сложнее и сложнее оказать. Вот почему каждый раз при ее использовании я повышаю цену. Ты желаешь забыть все, что знаешь?"] = "002e3e2a721f2f5fb8a4b9da6cdeb661",
		["Мое знание – твое знание, моя мудрость – твоя мудрость. Чем могу служить, $n?"] = "7f25bf796f093ccde831db9e50079867",
		["Да пребудет с тобой Элуна, $c."] = "9f0b1ccaf183a8e36a2849671ae33e26"
	},
	["Аянна Вечнобродка"] = {
		["С помощью строгой переквалификации мне пришлось заставить множество учеников забыть все, что они знали, чтобы я смогла учить их заново. Услугу, что я предлагаю, с каждым разом все сложнее и сложнее оказать. Вот почему каждый раз при ее использовании я повышаю цену. Ты желаешь забыть все, что знаешь?"] = "002e3e2a721f2f5fb8a4b9da6cdeb661",
		["Я не обучаю таких, как ты, $c."] = "9bd1614bca66fd26e163696ba1b6b6fc",
