	["Горкас"] = {
		["Ты не видишь – я здесь рыбачу!"] = "6d360eae76ce9d8dd443dda348dde45e",
		["Ты $gраспугал:распугала; всю рыбу! Чего тебе надо?"] = "fdcd1e64bf065be3a6c23042d93a5b68",
		["Чем могу быть полезен, $c?"] = "8954aa5e093a20511ce5bc5c69380aac",
		["Я буду здесь, пока не поймаю во-о-от такого окуня! А ты уже хочешь куда-то сорваться, верно?"] = "618fd179dc62675047c361ba163020cb"
	},
	["Нице"] = {
		["Куда бы вы хотели слетать?"] = "26d3fb863cf374254276fad4c4fb1b43"
