	["Калиб"] = {
		["Ах, $gдруг:подруга;, я помогаю только охотникам и их питомцам."] = "963db5ef5555edf879e137a605643024",
		["Ты хочешь учиться, чтобы передать знания своим питомцам?"] = "3108806b44f37624987d36cafc8de658"
	},
	["Балдрик"] = {
		["Куда бы вы хотели слетать?"] = "646a52da6f00e2b6b680741da2aab13f"
	},
	["Телорен"] = {
		["Куда бы вы хотели слетать?"] = "8ee9e2b8b1fbeba82851331d36cb251c"
	},
	["Распорядитель гонок Гореездник"] = {
		["Добро пожаловать на гонки! Надеемся, вам здесь понравится. Да, и не ставьте все деньги на одного!"] = "3fda9ba9ead86b9e29b7d1fc68875623",
		["Смелый не хочет, чтобы Тирт знал, что он все еще на Мерцающей равнине, поэтому он превратил себя в цыпленка!$B$BЕсли найдешь Смелого, попробуй подозвать его. Быть может, он выйдет из укрытия."] = "818428d85cc2451411dffed3878e6204",
		["Приветик, $c! Добро пожаловать на гонки Мерцающей равнины!"] = "cf552d2d1ba02525e8fcd3e41003294d",
		["У кого самая быстрая машина? Да кто ж знает?!"] = "f8abb3ccf31041b3183d41a1896f28e3",
		["Гонщики-гоблины обладают огромной мощью, но гномы всегда прячут пару-другую хитростей в их автомобилях…"] = "85279dcb34b8519c4aa585d439098471"
	},
	["Гоблин-автомеханик"] = {
		["А, вы имеете в виду Джонсона? Говорят, он сейчас в бегах."] = "38a01b6277bccedcf3bd8f0506a44706",
		["Что касается меня, я считаю гоблинов фаворитами. У нас есть и оборудование, и талант!"] = "ef38b09c14f46b5a8dcf5d782c630fd6",
		["Ха! Тирт уже который день его ищешь, но он все прячется, трус!"] = "4cf39f0b48e67e4fb4e5d3538ae49f07",
		["Если бы не это треклятое гномское везение, они бы ни одной гонки не выиграли!"] = "1707d93334e41f8adae340c8be1bc91a",
		["Ищешь 'Отчаянного'? Ладно, я тебе этого не говорил, но… расспроси Кронкрайдера в башне, он тебе расскажет…"] = "6ca0e5f286f5115ccba86010cc9d19a7",
		["Я бы на твоем месте поставил на гоблинов. Болеть за любую другую команду – пустая трата времени."] = "84067fc993444ce72a27b52f4f74bec4",
		["Мои деньги у гоблинов! И если ты хоть что-нибудь соображаешь, то и твои должны быть там же!"] = "8a6ae51d27ab952a233cd6f757870376",
		["Говорят, они собираются взорвать машину гномов в следующей гонке! Правда это или нет, но я буду держаться подальше от их боксов."] = "0c2296859db21b24eb9910aa4b0c2053"
	},
	["Механик гномского гаража"] = {
		["Тирт в последнее время сам не свой, и его помощник тоже."] = "329639fd7e8c50ac0b798afe6c0f563e",
		["Правду говорят: дуракам везет, иначе эти гоблины разбивали бы свои машины в каждой гонке!"] = "413ec8412f752d13ea7d3bc09e39592d",
		["Джонсон? Я думаю, он где-то здесь…"] = "5d227c61a514e3f0b41f912efe022c52",
		["Эти гонки дают отличную возможность продемонстрировать нашу изобретательность!"] = "88d6d4700d0a9435deac731bbef17f8c",
		["'Смелый'? Я думаю, что распорядитель гонок Гореездник наверху, в башне, знает, где он."] = "288dfdbe6b0b08a6f2781e22dc5d8ce0",
		["Я усовершенствовала матричный регулометр нашей машины. Теперь ее скорость должна возрасти как минимум в пятнадцать раз!"] = "bb2e2ac84fdaea93b4cb5f24ace4d539",
		["Да мой сын может собрать машину лучше, чем рухлядь этого гоблина!"] = "5a9bf6255acd34e980aebcd04a7a6def",
		["Мой технологический альманахотрон говорит, что мы выиграем у гоблинов на двадцать секунд больше, чем в прошлый раз!"] = "29cb9f241b67a6bd58f46757f27f6f9e"
	},
	["Парквел Финталлас"] = {
		["А теперь ответь мне, $n.$BКто стал Королем-личом еще до распространения чумы в Лордероне?"] = "f6f7ba02da1200cdb9a4345823966550",
		["Всегда приятно, когда кто-то желает со мной поговорить. Как у тебя дела, $c?"] = "b219a709c06746dd4308b42ceae14a51"
	},
	["Брауг Тусклый Дух"] = {
		["Я вот о чем хочу тебя спросить, $c:$Bкто из Аспектов, созданных титанами, превратился в мерзкое существо по имени Смертокрыл?"] = "8a2e3ce00848461a788a1c12e87e9577",
		["Приветствую, $c. Чем я могу помочь?"] = "06ed16ee9dc4b0c6ba6d2eea8e2d58cc"
	},
	["Верховный инквизитор Фэйрбанкс"] = {
		["*Верховный инквизитор Фэйрбанкс качает головой.* $B$BНет, $r. Лишь один из его сыновей мертв. Второй – жив…$B$B*Верховный инквизитор Фэйрбанкс указывает на небо.*$B$BЗапределье… Ищи его там…"] = "0405e4235c3c4375d54a46badf6f9a18",
		["Боюсь, что клинок, который вы держите, уже не спасти. Он насквозь пропитан ненавистью. Но не теряйте надежды, $c. Конец одного всегда означает начало другого.$B$BНайдите его сына – более преданного и набожного создания сейчас уже не встретишь. Говорят, что он может воссоздать Пеплоносца без старого запятнанного клинка."] = "2ec9cfd31d00a8b0299dc181a8190cd6",
		["Да, это так. В страхе перед младшим Могрейном я вернулся обратно в Монастырь Алого ордена. Я вопил и кричал. Я рассказывал эту историю любому, кто готов был слушать. И за это меня хладнокровно убили бы, затащив в эту комнату, мрачную тайну ордена. Но кто-то услышал… кто-то прислушался к моим словам. Так появился Серебряный Рассвет…"] = "91edc5312c9d7985a58d5ec06e446c8b",
		["Клинок и Могрейн были одним целым. Понимаешь? Это испортило клинок и привело к тому, что Могрейн превратился в рыцаря Смерти, служащего Кел'Тузаду. Я поклялся положить свою жизнь на то, чтобы истребить всех соучастников этого гнусного преступления. Два дня я пробыл в гнили и разложении Плети, пока не набрался достаточно сил для того, чтобы сбежать из разрушенного города."] = "53ad3252b278c0ae93d1b6b40ce3adc1",
		["Мальчишка подобрал Пеплоносец и ударил отца в спину, пронзив сердце. Последние слова Могрейна будут преследовать меня вечно: 'Что ты сделал, Рено? Почему?'"] = "bec302f02e2201f067255a714e311fff",
		["Все, что я мог сделать, – притвориться мертвым, в то время как на мне громоздились трупы Плети. Было темно, и я слышал лишь глухие звуки битвы надо мной. Лязг железа, грохот, скрежет… ужасные звуки. А потом все стихло. Он позвал меня! 'Фэйрбанкс! Фэрбенкс, где ты? Ответь мне, Фэйрбанкс!' А потом я услышал невероятный звук. Хватку предательства, $r…"] = "f95886b49c3b646b540936a1c253acc0",
		["<Великий инквизитор Фэйрбанкс качает головой.>$B$BНет… Он стоял сзади, наблюдая, как движется на нас легион нежити. Вскоре после этого мои силы иссякли. Я пал первым… Несомненно, меня разорвали бы на куски, пока я лежал без сознания; но их целью был наш предводитель – я их не интересовал…"] = "8dc3c54670f0b069274f87a57d834908",
		["Этого было мало.$B$B*Фэйрбанкс улыбается своим воспоминаниям.* $B$BВраги наступали тысячами – и тысячами погибали, сокрушенные Светом и мощью Могрейна! Он убивал каждого, кто приближался к нему. В этом хаосе я заметил, что младший Могрейн все еще здесь, чуть поодаль. Я крикнул ему: 'Рено, помоги! Помоги отцу, мальчик!'"] = "e174ab34785ee68b6e6d0710eca52df1",
		["Идиот, это был Пеплоносец! В то время как силы Плети материализовались вокруг нас, лезвие Могрейна начало сиять… звенеть… Более молодой Могрейн счел бы это знаком, что пора бежать. Они обрушились на нас, снедаемые яростью, какой я прежде никогда не видел. И все же…"] = "8e73eebbc5846e6d2b35af2153300f68",
		["*Великий инквизитор Фэйрбанкс поднимает гербовую накидку, обнажая ужасные шрамы.*$B$BПотому, что я был там… я был самым доверенным советником Высокого Лорда. Мне следовало бы знать… Я чувствовал что-то неладное и все же допустил, чтобы это случилось. Можешь ли ты поверить, что там оказалась тысяча воинов Плети, если не больше?"] = "51188fc10a87cced513453708b5ed8eb",
		["*Великий инквизитор Фэйрбанкс кивает.*$B$BДа, Младший Могрейн, известный как Командир Алых, как я и подозревал, вел дела с Великим Рыцарем. Он завел своего отца в засаду, словно ягненка на убой."] = "c6b0e8d92e327360ee6e22abe88e74a0",
		["Настоящий герой всегда погибает трагически, $r. Верховный рыцарь заключил договор с самим Кел'Тузадом! Они решили разыграть внезапное нападение, которое должно было закончиться гибелью Могрейна. Такое предательство может быть вызвано только действиями самых близких и преданных спутников."] = "351782cc2c2eab075255b02928b70aab",
		["Верховный лорд был центральной фигурой Алых. Да, Могрейна прозвали Пеплоносцем за его расправу над армией Короля-лича. Вооруженный лишь клинком и верой, Могрейн мог истребить целый батальон нежити – и уйти без единой царапины. От его врагов оставался только пепел. Ты можешь это понять? Сама смерть его боялась! Она трепетала в его присутствии!"] = "5d26127991e8911d63d93013418dcde7",
		["Главнокомандующий Аббендис, великий инквизитор Изиллиен и верховный лорд Могрейн создали Алый орден. Сначала он был благородным орденом. Того сумасшедшего и безумного фанатизма, что мы видим теперь, тогда не существовало. И так было до тех пор, пока один из них, известный как Верховный рыцарь, не показал на своем примере, что Скверна неостановима."] = "49e8b244faaa788e192bdbf757290769",
		["Да, верховный лорд Могрейн – основатель первоначального Алого ордена. Рыцарь непоколебимой веры и чистоты, Могрейн был предан своим собственным сыном и убит силами Кел'Тузада в Стратхольме. Вот как я оказался здесь…"] = "ef7daa42b41400a1595d39af4a6e8762",
		["Ты что, не знаешь? Меч, который ты носишь за спиной, зовется Пеплоносцем – по имени первого владельца."] = "5305da0d8750979d1bc134fec6194486",
		["Наконец-то проклятье снято! Спасибо вам!"] = "3146d1e3ce619ce4be9719884bfd59d3"
	},
	["Майкл Гарретт"] = {
		["Чем могу быть полезен?"] = "7c8c7304e2aa7ceedcd878cb5540437f"
	},
	["Эвника Барч"] = {
		["Я могу научить тебя готовить!"] = "02987be90f767850ec8e9f572adbc4ed",
		["'Поваренную книгу умельца' можно купить у Вулана в Деревне Ночных Охотников. Тебе она понадобится, если ты хочешь расти над собой."] = "fc64c85b44749257ced7e34a940a1c64",
		["Могиль Быстроруб из Прибамбасска – мастер кулинарных искусств. Тебе обязательно стоит его разыскать!"] = "9f8ebedd06a040d3d2ffa50aac35d51f",
		["Кулинария – страсть на всю жизнь. И, как я вижу, вы тоже не чужды этой страсти!"] = "f5400ec6ebea670b036a6880beada4ab"
	},
	["Гордон Уиндем"] = {
		["Приветствую – и добро пожаловать. У меня не очень-то выходит улыбаться, но поверьте, я счастлив вам служить."] = "f036f024de84a7992c236bf86b742eab"
	},
	["Каел Жнец Душ"] = {
		["С помощью строгой переквалификации мне пришлось заставить множество учеников забыть все, что они знали, чтобы я смог учить их заново. Услугу, что я предлагаю, с каждым разом все сложнее и сложнее оказать. Вот почему каждый раз при ее использовании я повышаю цену. Ты желаешь забыть все, что знаешь?"] = "3a364a4573d4825a6795e3bb454069d4",
		["$C, твой дух недостаточно крепок, чтобы управлять теми силами, с которыми имеем дело мы."] = "5a92f47cf481eb3def2615b8693c94fc",
		["$n, ты уже знаешь достаточно, чтобы быть $gопасным:опасной;, но тебе все равно стоит подучиться у меня."] = "f097ab331f582c2d79e805e164a7c169"
	},
	["Лютер Рудокоп"] = {
		["С помощью строгой переквалификации мне пришлось заставить множество учеников забыть все, что они знали, чтобы я смог учить их заново. Услугу, что я предлагаю, с каждым разом все сложнее и сложнее оказать. Вот почему каждый раз при ее использовании я повышаю цену. Ты желаешь забыть все, что знаешь?"] = "a54fabb9fc0936c92134f8611f4e5e8b",
