	["Гуляка Лунного фестиваля"] = {
		["Они собираются пускать фейерверки всю ночь напролет. С шести вечера и до утра небо над озером Элуны будет сверкать!"] = "3ebd4c9fbc15b539397a05f7c7e9bfb7",
